package assignment5;
import java.util.Scanner;
public class AssignmentOperator_p5{
public static void main(String[] args) {
// TODO Auto-generated method stub
int a = 10;
System.out.println("Assignment operator +=: "+ (a+=1));
System.out.println("Assignment operator -=: "+ (a-=1));
System.out.println("Assignment operator *=: "+ (a*=2));
System.out.println("Assignment operator /=: "+ (a/=2));
}
}